mapper.update:
  update annual_count set sum_count = sum_count + 1
where annual_date Between #{startDate} and #{endDate}
and department_uid in (select department_uid from department where department_name = #{departmentName});

update annual_count set sum_count = sum_count - 1
where annual_date Between #{startDate} and #{endDate}
and department_uid in (select department_uid from department where department_name = #{departmentName});
